import React, {Component} from 'react';
import Layout from '../../components/Layout';
import {Form, Button, Input, Message, Divider} from 'semantic-ui-react';
import Exchange from '../../ethereum/exchange';
import web3 from '../../ethereum/web3';
import { Router } from '../../routes';
// import Router from 'next/router'

class ExchangeNew extends Component {
    state = {
        PowerUnits: '',
        Price: '',
        errorMessage: '',
        loading: false
    };
    
    static async getInitialProps(props) {
        const {address} = props.query;
        

        return {address};
    }


    onSubmit = async event => {
        event.preventDefault();

        const exchange = Exchange(this.props.address);

        this.setState({loading: true, errorMessage:''});
        try{
            await ethereum.enable();
            const accounts = await web3.eth.getAccounts();

            await exchange.methods
                .createRequest(this.state.Price, this.state.PowerUnits).send({ from: accounts[0]});
            
           
            Router.pushRoute('/');

        }
        catch(err)
        {
            this.setState({errorMessage: err.message});
        } finally { 

            this.setState({loading: false});
           
        }
    };

    render() {
        return(
            <Layout>
                <h2>Create New Exchange</h2>
                <Divider hidden />
                <Divider inverted width={6}/>
                <Divider hidden />
                <Form onSubmit={this.onSubmit} error={!!this.state.errorMessage}>
                    <Form.Field width={6}>
                        <label>Power Units</label>
                        <Input 
                            label="Kw" 
                            labelPosition="right"
                            value={this.state.PowerUnits}
                            onChange={event => 
                                this.setState({ PowerUnits: event.target.value })}/>
                    </Form.Field>

                    <Form.Field width={6}>
                        <label>Price</label>
                        <Input 
                            label="wei" 
                            labelPosition="right"
                            value={this.state.Price}
                            onChange={event => 
                                this.setState({ Price: event.target.value })}/>
                    </Form.Field>
                    <Divider hidden />
                    <Divider inverted width={6}/>
                    <Divider hidden />
                    <Message error header="Oops!" content={this.state.errorMessage} />
                    <Button loading={this.state.loading} primary>Create</Button>
                </Form>
            </Layout>
        );
    }
}

export default ExchangeNew;
