import React, { Component } from 'react';
import { Button , Table} from 'semantic-ui-react';
import { Link } from '../../../routes';
import Layout from '../../components/Layout';
import Exchange from '../../ethereum/exchange';
import RequestRow from '../../components/RequestRow';

class ExchangeIndex extends Component {
    static async getInitialProps(props) {
        const { address } = props.query;

        const exchange = Exchange(address);
        const exchangesCount = await exchange.methods.getExchangesCount().call();

        const exchanges = await Promise.all(
            Array(parseInt(exchangesCount)).fill().map((element, index) => {
                return exchange.methods.deployedExchanges(index).call()
            })
        );

        console.log(exchanges);

        return { address , exchanges, exchangesCount};
    }

    renderRows(){
        return this.props.exchanges.map((request, index) => {
            return (
                <RequestRow 
                    id={index}
                    key={index}
                    exchange={exchange}
                    address={this.props.address}
                    
                />
            );
        });
    }

    render() {
        const {Header, Row, HeaderCell, Body } = Table;
        return (
            <Layout>
                <h2>My Exchanges List</h2>
                <Link route={`/Exchanges/new`}>
                    <a>
                        <Button primary floated="right" style={{marginBottom: 10}} >New Exchange</Button>                        
                    </a>
                </Link>
                <Table>
                    <Header>
                        <Row>
                            <HeaderCell>ID</HeaderCell>
                            <HeaderCell>Units (Kw)</HeaderCell>
                            <HeaderCell>Price (SDG)</HeaderCell>
                            <HeaderCell>complete</HeaderCell>
                        </Row>
                    </Header>
                    <Body>
                        {this.renderRows()}
                    </Body>
                </Table>
        <div>Found {this.props.exchangesCount} Exchanges.</div>
            </Layout>
        );
    }
}

export default ExchangeIndex;
