import React , {Component} from 'react';
import {Card , Button} from 'semantic-ui-react';
import factory from '../ethereum/factory';
import Layout from '../components/Layout';
import { Link } from '../routes';
import Exchange from '../ethereum/exchange';

// export default () => {
//     return <h1>This is the index page</h1>;
// };

class ExchangeIndex extends Component {

    static async getInitialProps(){
        const exchanges = await factory.methods.getDeployedExchanges().call();
        const address = exchanges[0];
            const exchange = Exchange(address);
            const requestCount = await exchange.methods.getRequestsCount().call();

            const requests = await Promise.all(
                Array(parseInt(requestCount)).fill().map((element, index) => {
                    return exchange.methods.exchangeRequest(index).call()
                })
            );


        return { exchanges ,requests , address};
    }
    
    state = {
        units: '',
        price: '',
        manager: '',
        loading: false,
        errorMessage:''
        // cardsItems: {}
    }

    renderCard() {
        const items = this.props.requests.map( (request) => {

            // const exchange = Exchange(address);
            // const summary = await exchange.methods.getSummary().call();
            //     // console.log(summary);
            return {
                    header: address,
                    description: 
                    <Button disabled={request.complete}  
                        loading = {this.state.loading}
                        color= "green"
                        basic 
                        floated="right"
                        onClick={this.onSubmit}> 
                        BUY
                    </Button>
                    ,
                    meta:  request.units + 'Kw | ' + request.price + 'wei',
                    fluid: true
                };
            });
            
            // const cardsItems = await Promise.all(items).then((values) => {
            //     console.log(values);
            //     return values;
            //   });
    
        // this.setState({ cardsItems });

        return <Card.Group items={items}/>
     }

    onSubmit = async event => {
        event.preventDefault();

        const exchange = Exchange(this.props.address);
        // const { manager, units, price} = exchange;

        this.setState({loading: true, errorMessage: ''});

        try {
            await ethereum.enable();
            const accounts = await web3.eth.getAccounts();
            await exchange.methods.FinalizeExchange(this.props.id).send({
                from : accounts[0]
            });
    

        Router.pushRoute(`/`)
        } catch (err) {

            this.setState({ errorMessage: err.message});
        }
        finally{

            this.setState({ loading: false});
        }
    }


    render() {
    return <div>
        <Layout>

            <h2>Pending Exchanges</h2>
            <Link route="/Exchanges/new">
                <a>
                    <Button 
                        content='Create new Exchange' 
                        icon='add' 
                        primary 
                        floated="right" 
                    />
                </a>
            </Link>
            {/* <Card.Group items={this.state.cardsItems}/> */}
                {this.renderCard()}
                
        </Layout>
        </div>;
    }
}

export default ExchangeIndex;
