import React , {Component} from 'react';
import {Card , Button} from 'semantic-ui-react';
import factory from '../ethereum/factory';
import Layout from '../components/Layout';
import { Link } from '../routes';
import Exchange from '../ethereum/exchange';

// export default () => {
//     return <h1>This is the index page</h1>;
// };

class ExchangeIndex extends Component {

    static async getInitialProps(props){
        const exchanges = await factory.methods.getDeployedExchanges().call();
        const {address} = props.query;
        
        return { exchanges , address};
    }
    
    state = {
        units: '',
        price: '',
        manager: '',
        loading: false,
        errorMessage:''
    }

    onSubmit = async event => {
        event.preventDefault();

        const exchange = Exchange(this.props.address);
        const { manager, units, price} = this.state;

        this.setState({loading: true, errorMessage: ''});

        try {
            await ethereum.enable();
            const accounts = await web3.eth.getAccounts();
            await exchange.methods.FinalizeExchange(this.props.id).send({
                from : accounts[0]
            });
    

        Router.pushRoute(`/`)
        } catch (err) {

            this.setState({ errorMessage: err.message});
        }
        finally{

            this.setState({ loading: false});
        }
    }


    renderExchanges() {
        
        const items = this.props.exchanges.map(async (address) => {

        const exchange = Exchange(address);
        const summary = await exchange.methods.getSummary().call();
            // console.log(summary);
        return {
                header: address,
                description: 
                <Button disabled={exchange.complete} 
                    color="green" 
                    basic 
                    floated="right"
                    onClick={this.onSubmit}> 
                    BUY
                </Button>
                ,
                meta:  summary[0] + 'Kw For ' + summary[1] + 'SDG',
                fluid: true
            };
        });
        const p = Promise.resolve(items);
        console.log(p);

        // const cardItems = Promise.all(p.map(Promise.all.bind(Promise)))
        // console.log(cardItems);
        // return <Card.Group items={cardItems}/>;
        return <Card.Group items={p}/>;
        
    }

    render() {
    return <div>
        <Layout>

            <h2>Pending Exchanges</h2>
            <Link route="/Exchanges/new">
                <a>
                    <Button 
                        content='Create new Exchange' 
                        icon='add' 
                        primary 
                        floated="right" 
                    />
                </a>
            </Link>
            {this.renderExchanges()}
                
                
        </Layout>
        </div>;
    }
}

export default ExchangeIndex;
